            title: "Edureka September Course",
            keyword: "User text goes here"
        }

    }

    render() {
        return (
            <Fragment>
                <header style={mystyle.header}>
                <div style={mystyle.logo}>{this.state.title}</div>
                <center>
                    <p>{this.state.keyword}</p>
                </center>
                <hr />
                </header>
            </Fragment>
        )
    }

}

